/*! GSAP Loader - Çankaya Nakliyat */
(function () {
  if (window.gsap) return;
  var script = document.createElement('script');
  script.src = 'https://cdnjs.cloudflare.com/ajax/libs/gsap/3.12.2/gsap.min.js';
  script.async = true;
  script.onload = function () {
    console.info('GSAP başarıyla yüklendi.');
    document.dispatchEvent(new CustomEvent('gsap:loaded', { detail: { success: true } }));
  };
  script.onerror = function () {
    console.warn('GSAP yüklenemedi. Temel animasyonlar devre dışı bırakıldı.');
    document.dispatchEvent(new CustomEvent('gsap:loaded', { detail: { success: false } }));
    window.gsap = undefined;
  };
  document.head.appendChild(script);
})();
